    <p>
      {props.name} {props.exercises}
    </p>
  )
} 

const Header = (props) => {
  return (
    <header>
      <h1>{props.course}</h1>
    </header>
  )
}

const Content = (props) => {
console.log(props.parts)
  let mapped = props.parts.map((item, index )=> {
    return (
    <Part key={index} name={item.name} exercises={item.exercises} />
    )
  })

  return (
    <section>
      {mapped}
    </section>
  )
}

const Total = (props) => {
  console.log(props)
