const Total = (props) => {
    console.log(props)
    const totalSum = props.part.reduce((a, b) => a + b.exercises, 0)

    return (
      <section>
        <p>Number of exercises {totalSum}</p>
      </section>
    )
  }
const Part = (props) => {
    return (
      <p>
        {props.name} {props.exercises}
      </p>
    )
  } 
function Course() {
    const course = [
        {
          name: 'Half Stack application development',
          id: 1,
          parts: [
            {
              name: 'Fundamentals of React',
              exercises: 10,
              id: 1
            },
            {
              name: 'Using props to pass data',
              exercises: 7,
              id: 2
            },
            {
              name: 'State of a component',
              exercises: 14,
              id: 3
            },
            {
              name: 'Redux',
              exercises: 11,
              id: 4
            }
          ]
        }, 
        {
          name: 'Node.js',
          id: 2,
          parts: [
            {
              name: 'Routing',
              exercises: 3,
              id: 1
            },
            {
              name: 'Middlewares',
              exercises: 7,
              id: 2
            }
          ]
        }
      ]

      const mapped = course[0].parts.map(item => <p>{item.name} {item.exercises}</p>)

    return (
        <p>
            {mapped}
        </p>

    )
}

export default Course
