import React from "react"
import Course from "./components/Course"



const Header = (props) => {
  return (
    <header>
      <h1>{props.course}</h1>
    </header>
  )
}

const Content = (props) => {
console.log(props.parts)
  let mapped = props.parts.map((item, index )=> {
    return (
    <Part key={index} name={item.name} exercises={item.exercises} />
    )
  })

  return (
    <section>
      {mapped}
    </section>
  )
}

const Total = (props) => {
  console.log(props)
  const totalSum = props.part.reduce((a, b) => a + b.exercises, 0)

  return (
    <section>
      <p>Number of exercises {totalSum}</p>
    </section>
  )
}

function App(props) {

console.log(props)


  return (
    <div className="App">
      <Header course={course[0].name} />
      <Content parts={course[0].parts} />
      <Total part={course[0].parts} />
    </div>
  );
}

export default App;
